import React, { useState } from 'react';

function AddProductForm({ onAddProduct }) {
  const [name, setName] = useState('');
  const [code, setCode] = useState('');
  const [ingredients, setIngredients] = useState([{ name: '', effect: '', hazard: false }]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddProduct({ name, code, ingredients });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input placeholder="Product Code" value={code} onChange={(e) => setCode(e.target.value)} />
      {/* Map through ingredients here with fields for name, effect, and hazard */}
      <button type="submit">Add Product</button>
    </form>
  );
}

export default AddProductForm;
