import React from 'react';
import './ProductDetail.css';

function ProductDetail({ product }) {
  const hazardousIngredients = product.ingredients.filter((ingredient) => ingredient.hazard);
  const safeIngredients = product.ingredients.filter((ingredient) => !ingredient.hazard);

  return (
    <div className="product-detail">
      <h2>{product.name}</h2>
      <h3>Ingredients:</h3>

      <ul className="ingredients-list">
        {safeIngredients.map((ingredient, index) => (
          <li key={index}>
            {ingredient.name}: {ingredient.effect}
          </li>
        ))}
      </ul>

      {hazardousIngredients.length > 0 && (
        <>
          <h3 className="hazardous-heading">Hazardous Ingredients:</h3>
          <ul className="hazardous-list">
            {hazardousIngredients.map((ingredient, index) => (
              <li key={index}>
                {ingredient.name}: {ingredient.effect} - Hazardous
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default ProductDetail;
