import React from 'react';
import { useParams } from 'react-router-dom';
import { AlertTriangle, Sprout, Flask, Shield } from 'lucide-react';

interface Ingredient {
  name: string;
  effect: string;
  hazardous: boolean;
  hazardReason?: string;
}

interface Product {
  id: number;
  name: string;
  code: string;
  type: string;
  description: string;
  hazardLevel: string;
  ingredients: Ingredient[];
  soilEffects: string[];
  plantEffects: string[];
}

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = React.useState<Product | null>(null);

  React.useEffect(() => {
    fetch(`http://localhost:3000/products/${id}`)
      .then(res => res.json())
      .then(setProduct);
  }, [id]);

  if (!product) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="product-details">
      <header className="product-header">
        <div className="product-title">
          <h1>{product.name}</h1>
          <span className="product-code">Code: {product.code}</span>
        </div>
        <div className={`hazard-badge ${product.hazardLevel.toLowerCase()}`}>
          <AlertTriangle />
          {product.hazardLevel} Risk Level
        </div>
      </header>

      <div className="product-content">
        <section className="info-section">
          <h2>
            <Flask className="section-icon" />
            Ingredients
          </h2>
          <div className="ingredients-list">
            {product.ingredients.map((ingredient, index) => (
              <div key={index} className={`ingredient-card ${ingredient.hazardous ? 'hazardous' : ''}`}>
                <h3>{ingredient.name}</h3>
                <p>{ingredient.effect}</p>
                {ingredient.hazardous && (
                  <div className="hazard-warning">
                    <AlertTriangle size={16} />
                    <p>{ingredient.hazardReason}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>

        <section className="info-section">
          <h2>
            <Sprout className="section-icon" />
            Effects on Soil
          </h2>
          <ul className="effects-list">
            {product.soilEffects.map((effect, index) => (
              <li key={index}>{effect}</li>
            ))}
          </ul>
        </section>

        <section className="info-section">
          <h2>
            <Shield className="section-icon" />
            Effects on Plants
          </h2>
          <ul className="effects-list">
            {product.plantEffects.map((effect, index) => (
              <li key={index}>{effect}</li>
            ))}
          </ul>
        </section>
      </div>
    </div>
  );
}

export default ProductDetails;
